import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { useGameState } from "@/hooks/use-game-state";
import { JudgeSelection } from "@/components/game/judge-selection";
import { GamePlay } from "@/components/game/game-play";
import { GameEnd } from "@/components/game/game-end";
import { type Player } from "@shared/schema";

interface GameProps {
  params: { code: string };
}

export default function Game({ params }: GameProps) {
  const [, setLocation] = useLocation();
  const [currentPlayer, setCurrentPlayer] = useState<Player | null>(null);
  const {
    gameState,
    setCurrentPlayer: setGameStatePlayer,
    joinRoom,
    revealNumberCard,
    startRound,
    selectPhotoCard,
    submitCaptionCard,
    exchangeCard,
    selectWinner,
    restartGame,
  } = useGameState();

  useEffect(() => {
    // Get current player from localStorage
    const storedPlayer = localStorage.getItem("currentPlayer");
    if (storedPlayer) {
      const player = JSON.parse(storedPlayer);
      setCurrentPlayer(player);
      setGameStatePlayer(player);
      joinRoom(player.id, player.roomId);
    } else {
      // Redirect to home if no player info
      setLocation("/");
    }
  }, []);

  // Update current player when game state changes
  useEffect(() => {
    if (gameState && currentPlayer) {
      const updatedCurrentPlayer = gameState.players.find(
        (p) => p.id === currentPlayer.id,
      );
      if (
        updatedCurrentPlayer &&
        JSON.stringify(updatedCurrentPlayer) !== JSON.stringify(currentPlayer)
      ) {
        setCurrentPlayer(updatedCurrentPlayer);
      }
    }
  }, [gameState, currentPlayer]);

  const handlePlayAgain = () => {
    // Reset game state on server - all players will be redirected automatically
    restartGame();
    // Note: Don't redirect here, let the game_restarted message handle it for all players
  };

  const handleNewRoom = () => {
    // Clear player info and return to home
    localStorage.removeItem("currentPlayer");
    localStorage.removeItem("playerName");
    setLocation("/");
  };

  if (!gameState || !currentPlayer) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin w-12 h-12 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4"></div>
          <p className="text-lg font-semibold text-foreground">
            Loading game...
          </p>
        </div>
      </div>
    );
  }

  // Check if all players have revealed their number cards
  const allRevealed = gameState.players.every((p) => p.numberCard !== null);

  // Find judge player (lowest number card)
  const judgePlayer = allRevealed
    ? gameState.players.reduce((lowest, player) =>
        (player.numberCard || 999) < (lowest.numberCard || 999)
          ? player
          : lowest,
      )
    : null;

  // Calculate game duration (mock for now)
  const gameTime = "12:34";

  if (gameState.room.status === "finished") {
    const winner = gameState.players.find((p) => p.trophies >= 5);
    if (winner) {
      return (
        <GameEnd
          winner={winner}
          players={gameState.players}
          totalRounds={gameState.room.currentRound}
          gameTime={gameTime}
          onPlayAgain={handlePlayAgain}
          onNewRoom={handleNewRoom}
        />
      );
    }
  }

  if (gameState.room.status === "selecting_judge") {
    return (
      <JudgeSelection
        players={gameState.players}
        currentPlayerId={currentPlayer.id}
        onRevealCard={revealNumberCard}
        onStartRound={startRound}
        allRevealed={allRevealed}
        judgePlayer={judgePlayer}
      />
    );
  }

  if (gameState.room.status === "playing") {
    return (
      <GamePlay
        gameState={gameState}
        currentPlayerId={currentPlayer.id}
        onSelectPhotoCard={selectPhotoCard}
        onSubmitCaptionCard={submitCaptionCard}
        onExchangeCard={exchangeCard}
        onSelectWinner={selectWinner}
      />
    );
  }

  return (
    <div className="min-h-screen bg-background flex items-center justify-center">
      <div className="text-center">
        <h1 className="text-2xl font-bold text-foreground mb-4">
          Game Status: {gameState.room.status}
        </h1>
        <p className="text-muted-foreground">
          Waiting for game state to update...
        </p>
      </div>
    </div>
  );
}

client/src/pages/home.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent } from "@/components/ui/card";
import { RulesModal } from "@/components/game/rules-modal";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Plus, LogIn, HelpCircle, Smile } from "lucide-react";

export default function Home() {
  const [, setLocation] = useLocation();
  const [playerName, setPlayerName] = useState("");
  const [roomCode, setRoomCode] = useState("");
  const [showRules, setShowRules] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const handleCreateRoom = async () => {
    if (!playerName.trim()) {
      toast({
        variant: "destructive",
        title: "Name required",
        description: "Please enter your name to create a room."
      });
      return;
    }

    setIsLoading(true);
    try {
      // Create room with temporary host ID, then join and update
      const response = await apiRequest("POST", "/api/rooms", {
        hostId: "temp-host-id", // Will be updated when first player joins
      });

      const { room } = await response.json();

      // Join the room as a player
      const joinResponse = await apiRequest("POST", `/api/rooms/${room.code}/join`, {
        name: playerName.trim(),
        isHost: true // Mark this player as the host
      });

      const { player } = await joinResponse.json();

      // Store player info in localStorage
      localStorage.setItem("currentPlayer", JSON.stringify(player));
      localStorage.setItem("playerName", playerName.trim());

      setLocation(`/lobby/${room.code}`);
    } catch (error) {
      toast({
        variant: "destructive", 
        title: "Failed to create room",
        description: "Please try again."
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleJoinRoom = async () => {
    if (!playerName.trim()) {
      toast({
        variant: "destructive",
        title: "Name required", 
        description: "Please enter your name to join a room."
      });
      return;
    }

    if (!roomCode.trim()) {
      toast({
        variant: "destructive",
        title: "Room code required",
        description: "Please enter a room code to join."
      });
      return;
    }

    setIsLoading(true);
    try {
      const response = await apiRequest("POST", `/api/rooms/${roomCode.trim().toUpperCase()}/join`, {
        name: playerName.trim()
      });

      const { player } = await response.json();

      // Store player info in localStorage
      localStorage.setItem("currentPlayer", JSON.stringify(player));
      localStorage.setItem("playerName", playerName.trim());

      setLocation(`/lobby/${roomCode.trim().toUpperCase()}`);
    } catch (error) {
      toast({
        variant: "destructive",
        title: "Failed to join room", 
        description: "Room not found or game in progress."
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary via-accent to-secondary flex items-center justify-center p-4">
      <div className="max-w-lg w-full">
        <div className="text-center mb-8">
          <h1 className="text-6xl font-bold text-white mb-4">
            <Smile className="inline mr-3 h-16 w-16" />
            Meme Masters
          </h1>
          <p className="text-xl text-white/90">The funniest multiplayer card game ever!</p>
        </div>

        <Card className="shadow-2xl">
          <CardContent className="p-8 space-y-6">
            <div className="text-center">
              <h2 className="text-2xl font-semibold text-foreground mb-2">Join the Fun</h2>
              <p className="text-muted-foreground">Create a room or join your friends</p>
            </div>

            {/* Create Room */}
            <div className="space-y-4">
              <div>
                <Label htmlFor="playerName" className="block text-sm font-medium text-foreground mb-2">
                  Your Name
                </Label>
                <Input
                  id="playerName"
                  type="text"
                  placeholder="Enter your name"
                  value={playerName}
                  onChange={(e) => setPlayerName(e.target.value)}
                  className="w-full"
                  data-testid="player-name-input"
                />
              </div>

              <Button
                onClick={handleCreateRoom}
                disabled={isLoading}
                className="w-full bg-primary text-primary-foreground py-3 hover:bg-primary/90 transform hover:scale-105"
                data-testid="create-room-button"
              >
                <Plus className="mr-2 h-4 w-4" />
                Create New Room
              </Button>
            </div>

            <div className="relative">
              <div className="absolute inset-0 flex items-center">
                <div className="w-full border-t border-border"></div>
              </div>
              <div className="relative flex justify-center text-sm">
                <span className="px-2 bg-card text-muted-foreground">or</span>
              </div>
            </div>

            {/* Join Room */}
            <div className="space-y-4">
              <div>
                <Label htmlFor="roomCode" className="block text-sm font-medium text-foreground mb-2">
                  Room Code
                </Label>
                <Input
                  id="roomCode"
                  type="text"
                  placeholder="Enter room code"
                  value={roomCode}
                  onChange={(e) => setRoomCode(e.target.value.toUpperCase())}
                  className="w-full font-mono"
                  data-testid="room-code-input"
                />
              </div>

              <Button
                onClick={handleJoinRoom}
                disabled={isLoading}
                className="w-full bg-accent text-accent-foreground py-3 hover:bg-accent/90"
                data-testid="join-room-button"
              >
                <LogIn className="mr-2 h-4 w-4" />
                Join Room
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Game Rules Preview */}
        <div className="mt-8 text-center">
          <Button
            variant="ghost"
            onClick={() => setShowRules(true)}
            className="text-white/80 hover:text-white hover:bg-white/10"
            data-testid="show-rules-button"
          >
            <HelpCircle className="mr-2 h-4 w-4" />
            How to Play
          </Button>
        </div>
      </div>

      <RulesModal isOpen={showRules} onClose={() => setShowRules(false)} />
    </div>
  );
}

